import React, { Component } from 'react';
import 
{
    Container,
    Segment,
    Form,
    Button,
    Grid,
    Header
} from 'semantic-ui-react';

export default class Login extends Component 
{
    constructor(props)
    {
        super(props)

        this.state = {
            isAuthenticated: true
        }
    }

    authenticate = (cb) => 
    {
        this.setState({ isAuthenticated: true })
        console.log(this.state.isAuthenticated)
        setTimeout(cb, 100)
    }

    signout = (cb) => 
    {
        this.setState(() => ({ isAuthenticated: false }))
        setTimeout(cb, 100)
    }

    render() 
    {
        return (
            <Container>
                <Grid centered>
                    <Segment 
                        style={{width:'400px', marginTop:'20px', borderWidth:'60px 40px', borderRadius:'80px 40px'}}
                        placeholder
                    >
                        <p>You must log in to view the page</p>
                        <Form>
                        <Form.Field>
                            <label>Email</label>
                            <input placeholder='email' />
                        </Form.Field>
                        <Form.Field>
                            <label>Password</label>
                            <input placeholder='password' />
                        </Form.Field>
                        <Button 
                            type='submit'
                            primary
                            // style={{backgroundColor:'#d1f4f6'}}
                        >
                            Login
                        </Button>
                        </Form>
                    </Segment>
                </Grid>
            </Container>
        )
    }
}
