import React, { Component } from 'react';
import 
{
    Container,
    Segment,
    Form,
    Button,
    Grid,
    Header
} from 'semantic-ui-react';

export default class Login extends Component 
{
    constructor(props)
    {
        super(props)

        this.state = {
            isAuthenticated: true
        }
    }

    authenticate = (cb) => 
    {
        this.setState({ isAuthenticated: true })
        console.log(this.state.isAuthenticated)
        setTimeout(cb, 100)
    }

    signout = (cb) => 
    {
        this.setState(() => ({ isAuthenticated: false }))
        setTimeout(cb, 100)
    }

    render() 
    {
        return (
            <Container>
                <Grid centered>
                    <Segment 
                        style={{width:'330px', marginTop:'20px', borderRadius:'20px 20px'}}
                    >

                        <Header 
                            as='h1' 
                            style={{color:'#40c5cd'}}
                        >
                            Saran Thai
                        </Header>

                        <p>Please enter admin sign in details</p>

                        <Form>
                        <Form.Field>
                            <label>Email</label>
                            <input placeholder='email' />
                        </Form.Field>
                        <Form.Field>
                            <label>Password</label>
                            <input placeholder='password' />
                        </Form.Field>
                        <Button 
                            style={{marginTop:'20px', marginBottom:'20px'}}
                            type='submit'
                            style={{backgroundColor:'#40c5cd', color:'#ffffff'}}                            
                        >
                            Login
                        </Button>
                        </Form>
                    </Segment>
                </Grid>
            </Container>
        )
    }
}
